import React from "react";
import { useCookies } from "react-cookie";
import { useEffect } from "react";
import Viewmyngo from "./viewurngo";

// a slide object contains the image link, title and function/click event for when a user clicks on a card
// const slides = [
//     {image:"https://picsum.photos/200/300",title:"This is a title",description:"This is a description",clickEvent:sliderClick},
//     {image:"https://picsum.photos/600/500",title:"This is a second title",description:"This is a second description",clickEvent:sliderClick},
//     {image:"https://picsum.photos/700/600",title:"This is a third title",description:"This is a third description",clickEvent:sliderClick},
//     {image:"https://picsum.photos/500/400",title:"This is a fourth title",description:"This is a fourth description",clickEvent:sliderClick},
//     {image:"https://picsum.photos/200/300",title:"This is a fifth title",description:"This is a fifth description",clickEvent:sliderClick},
//     {image:"https://picsum.photos/800/700",title:"This is a sixth title",description:"This is a sixth description",clickEvent:sliderClick},
//     {image:"https://picsum.photos/300/400",title:"This is a seventh title",description:"This is a seventh description",clickEvent:sliderClick},
// ]




export default function Viewprofile(){
    const[token, settoken] = useCookies(['mytoken'])
    const[tokenid, settokenid] = useCookies(['myid'])
    const[profile, setprofile] = React.useState({})

    const Card = (props) => (
        <div className="card">
          <img src={ props.imgUrl } 
            alt={ props.alt || 'Image' } />
          <div className="card-content">
            <h2>{ props.title }</h2>
            <p>{ props.content }</p>
          </div>
        </div>
      );

      const CardContainer = (props) => (
        <div className="cards-container">
          {
            props.cards.map((card) => (
              <Card title={ card.title }
                content={ card.content }
                imgUrl={ card.imgUrl } />
            ))
          }
        </div>
      );

      const cardsData = [
        {id: 1, title: 'CARD 1', content: 'Clark Kent', imgUrl: 'https://unsplash.it/200/200'},
        {id: 2, title: 'CARD 2', content: 'Bruce Wayne', imgUrl: 'https://unsplash.it/201/200'},
        {id: 3, title: 'CARD 3', content: 'Peter Parker', imgUrl: 'https://unsplash.it/200/201'},
        {id: 4, title: 'CARD 4', content: 'Tony Stark', imgUrl: 'https://unsplash.it/201/201'},
        {id: 5, title: 'CARD 5', content: 'Reed Richards', imgUrl: 'https://unsplash.it/202/200'},
        {id: 6, title: 'CARD 6', content: 'Wade Wilson', imgUrl: 'https://unsplash.it/200/199'},
        {id: 7, title: 'CARD 7', content: 'Peter Quill', imgUrl: 'https://unsplash.it/199/199'},
        {id: 8, title: 'CARD 8', content: 'Steven Rogers', imgUrl: 'https://unsplash.it/199/200'},
        {id: 9, title: 'CARD 9', content: 'Bruce Banner', imgUrl: 'https://unsplash.it/200/198'},
        {id: 10, title: 'CARD 10', content: 'Vincent Strange', imgUrl: 'https://unsplash.it/198/199'},
      ]


    useEffect(() => {
        fetch(`http://127.0.0.1:8000/user/${tokenid['myid']}`, {
        'method' : 'GET'
        })
            .then(res => res.json())
            .then(data => setprofile(data))
    }, [])
    if(token['mytoken'])
    {
        // const [profile, setprofile]
        // useEffect(() => {
        //     fetch(`http://127.0.0.1:8000/user/${tokenid['myid']}`, {
        //     'method' : 'GET'
        //     })
        //         .then(res => res.json())
        //         .then(data => console.log(data))
        // }, [])
        return(
            <div>
                <h1>{profile.first_name} {profile.last_name}</h1>
                <h5>{profile.username}</h5>
                <h3>Email- {profile.email}</h3>
                <h1>My list</h1>
                <div className="container">
        <h1 style={{ 'text-align': 'center' }}>
          React Card Slider
        </h1>

        <CardContainer cards={ cardsData } />
      </div>
            </div>
        )
    }
}
