import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import '../App.css'

export default function Reg(){
    const[name, setname] = useState('')
    const[password, setpassword] = useState('')
    const [response, setresponse] = useState({
        username: '',
        password: ''
    })
    const [count, setcount] = useState(1)
    function getusername(event){
        setname(event.target.value)
    }
    function getpassword(event){
        setpassword(event.target.value)
    }
    function but(){
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ username:`${name}`, password : `${password}`})
        };
        fetch(`http://127.0.0.1:8000/user/`, requestOptions)
        .then(res => res.json())
        .then(data => setresponse(data))

        // setcount(1)
        if(response.username === 'This field may not be blank.' || response.password === 'This field may not be blank.'|| response.username==='A user with that username already exists.' || response.username===''){
           setcount(1)
        }
        else{
            setcount(0)
        }
        setname('')
        setpassword('')

    }
    // useEffect(() => {
    //     const requestOptions = {
    //         method: 'POST',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: JSON.stringify({ username:`${name}`, password : `${password}`})
    //     };
    //     fetch(`http://127.0.0.1:8000/user/`, requestOptions)
    //     .then(res => res.json())
    //     .then(data => console.log(data))
    // }, [getusername]);

    return(
        <div className="reg1">
            <div className="reg">
                <input type='text' onChange={getusername} placeholder='Username' value={name}/>
                <input type='password' onChange={getpassword} placeholder='Password' value={password} />
                <button onClick={but}>Next</button>

                {count===1?response.username : 'successfull'}
            </div>
            </div>
    )
}
