import React from "react";
import { useCookies } from "react-cookie"; 
import Ngo_list from "./ngo-list";
import { useState } from "react";
import { useEffect } from "react";

export default function Viewmyngo(){

    const [tokenid, settokenid] = useCookies('myid')
    const [myngo, setmyngo] = useState([])
    let ownerid = tokenid['myid']
    console.log(ownerid)
    const [ngoss, setngoss] = useState([])
    useEffect(() => {
        fetch("http://127.0.0.1:8000/ngo-list/" , {
        'method' : 'GET',

        })
            .then(res => res.json())
            .then(data => {
                data.map((item) =>{
                    if(ownerid == item.owner){
                        return setmyngo(prev => [...prev, item])
                    }
                })

            })
    }, [])
    const renderList = myngo.map((item) =>{

        return(


            <div> 

                {ownerid == item.owner && <Ngo_list title={item.title} aim={item.aim} img = {item.coverimg} id={item.id} owner={item.owner}/>}
            </div>

        )


    });
    return(
        <div className="fullngolist">
            <div className="nnggoo">
                <h1>{ownerid}</h1>
                {renderList}

            </div>
        </div>
    )
}
