import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import '../App.css'
import Eachngo from "./eachngo";
import { Route , Routes} from "react-router-dom";

export default function Ngo_list(props){
    const[ngoget, setngoget] = React.useState({})
    const navigate = useNavigate();
    function Getthengo(id){

            fetch(`http://127.0.0.1:8000/ngo-list/${id}/`, {
            'method' : 'GET'
            })
                .then(res => res.json())
                .then(data => setngoget(data))
            navigate(`/ngo-list/${id}/`)
            return(

                   <Eachngo title={ngoget.title}/> 

            )
        }

    return(
        <div className="ngo-item" onClick={()=>Getthengo(props.id)}>
            <img src={props.img} alt='No Image!!'/>
            <h1>{props.title}</h1>
            <h5>Aim:- {props.aim}</h5>
            <p>Click to get full profile of this ngo</p>
        </div>
    )
}
