import React from "react";
import { useCookies } from "react-cookie"; 
import Ngo_list from "./ngo-list";
import { useState } from "react";
import { useEffect } from "react";

export default function Viewmyngo(){
    const [token, settoken] = useCookies('mytoken')
    const [tokenid, settokenid] = useCookies('myid')

    const [ngos, setngos] = useState([])
    useEffect(() => {
        fetch("http://127.0.0.1:8000/ngo-list/", {
        'method' : 'GET'
        })
            .then(res => res.json())
            .then(data => setngos(data))
    }, [])
    const renderList = ngos.map((item) =>{
        console.log(item.owner)
        return(
            <div>
            { tokenid['myid'] === item.owner &&
            <div>
                <Ngo_list title={item.title} aim={item.aim} img = {item.coverimg} id={item.id}/>
            </div>}
            </div>
        )
    });
}
