import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import '../App.css'

export default function Reg(){
    const[name, setname] = useState('')
    const[password, setpassword] = useState('')
    const [response, setresponse] = useState({
        username: '',
        password: ''
    })
    const [count, setcount] = useState(1)
    const [resStatus, setResStatus] = useState()
    const [step, setstep] = useState(1)

    function getusername(event){
        setname(event.target.value)
    }
    function getpassword(event){
        setpassword(event.target.value)
    }
    function but(){
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ username:`${name}`, password : `${password}`})
        };
        fetch(`http://127.0.0.1:8000/user/`, requestOptions)
        .then(res => {
            setResStatus(res.status)
            return(
                res.json()
            )
        })
        .then(data => setresponse(data))

        // setcount(1)
        if(resStatus===200){
           setcount(0)
           setstep(2)
           setname('')
           setpassword('')
        }
        else{
            setcount(1)
            setstep(0)
        }

    }
    // useEffect(() => {
    //     const requestOptions = {
    //         method: 'POST',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: JSON.stringify({ username:`${name}`, password : `${password}`})
    //     };
    //     fetch(`http://127.0.0.1:8000/user/`, requestOptions)
    //     .then(res => res.json())
    //     .then(data => console.log(data))
    // }, [getusername]);

    return(
        <div className="reg1">
            {step===1 &&
            <div className="reg">
                <input type='text' onChange={getusername} placeholder='Username' value={name}/>
                {count===1 && <p className="errormsg">{response.username}</p>}
                <input type='password' onChange={getpassword} placeholder='Password' value={password} />
                {count===1 && <p className="errormsg">{response.password}</p>}
                <button onClick={but}>Next</button>
            </div>}
            {step===2 &&
            <div className="reg">
                hello
            </div>}

            </div>
    )
}
